mapper.insert:
INSERT INTO category (
	CATEGORY_NAME,
	INS_USER
) VALUES (
	#{entity.categoryName},
	#{entity.insUser}
)

mapper.update:
UPDATE category


		CATEGORY_NAME = #{entity.categoryName},

	UPD_TIME = CURRENT_TIMESTAMP,
	UPD_USER = #{entity.updUser}


	ID = #{entity.id}

UPDATE category

	DEL_FLAG = 1,
	DEL_TIME = CURRENT_TIMESTAMP,
	DEL_USER = #{entity.delUser}


	ID = #{entity.id}
