mapper.select:
SELECT ID AS id, TANK_ID AS tankId, SHIFT AS shift, PRICE_ID AS priceId, METER_OLD AS meterOld, METER_NEW AS meterNew, METER_ELEC_OLD AS meterElecOld, METER_ELEC_NEW AS meterElecNew, INS_TIME AS insTime, INS_USER AS insUser, UPD_TIME AS updTime, UPD_USER AS updUser, DEL_FLAG AS delFlag, DEL_TIME AS delTime, DEL_USER AS delUser FROM daily_meter
ID = #{map.id}
AND TANK_ID = #{map.tankId}
AND SHIFT = #{map.shift}
AND PRICE_ID = #{map.priceId}
AND METER_OLD = #{map.meterOld}
AND METER_NEW = #{map.meterNew}
AND METER_ELEC_OLD = #{map.meterElecOld}
AND METER_ELEC_NEW = #{map.meterElecNew}
AND INS_TIME::DATE = #{map.insTime}::DATE
AND INS_TIME::DATE = #{map.dateFrom}::DATE
AND INS_TIME::DATE >= #{map.dateFrom}::DATE AND INS_TIME::DATE <= #{map.dateTo}::DATE
AND EXTRACT (MONTH FROM INS_TIME) = EXTRACT (MONTH FROM #{map.monthFrom}::DATE)
AND EXTRACT (MONTH FROM INS_TIME) >= EXTRACT (MONTH FROM #{map.monthFrom}::DATE) AND EXTRACT (MONTH FROM INS_TIME) <= EXTRACT (MONTH FROM #{map.monthTo}::DATE)
AND DEL_FLAG = 0

mapper.update:
UPDATE daily_meter

	DEL_FLAG = 1,
	DEL_TIME = CURRENT_TIMESTAMP,
	DEL_USER = #{entity.delUser}


	ID = #{entity.id}
