mapper.select:
SELECT ID AS id, PRODUCT_ID AS productId, PRICE AS price, INS_TIME AS insTime, INS_USER AS insUser, UPD_TIME AS updTime, UPD_USER AS updUser, DEL_FLAG AS delFlag, DEL_TIME AS delTime, DEL_USER AS delUser FROM product_price
ID = #{map.id}
AND PRODUCT_ID = #{map.productId}
AND PRICE = #{map.price}
AND DEL_FLAG = 0

mapper.insert:
INSERT INTO product_price (
	PRODUCT_ID,
	PRICE,
	INS_USER
) VALUES (
	#{entity.productId},
	#{entity.price},
	#{entity.insUser}
)

mapper.update:
UPDATE product_price


		PRODUCT_ID = #{entity.productId},


		PRICE = #{entity.price},

	UPD_TIME = CURRENT_TIMESTAMP,
	UPD_USER = #{entity.updUser}


	ID = #{entity.id}

UPDATE product_price

	DEL_FLAG = 1,
	DEL_TIME = CURRENT_TIMESTAMP,
	DEL_USER = #{entity.delUser}


	ID = #{entity.id}
