mapper.select:
SELECT ID AS id, PRODUCT_ID AS productId, TANK_NAME AS tankName, INS_TIME AS insTime, INS_USER AS insUser, UPD_TIME AS updTime, UPD_USER AS updUser, DEL_FLAG AS delFlag, DEL_TIME AS delTime, DEL_USER AS delUser FROM tank
ID = #{map.id}
AND PRODUCT_ID = #{map.productId}
AND TANK_NAME LIKE '%' || #{map.tankName} || '%'
AND DEL_FLAG = 0

mapper.insert:
INSERT INTO tank (
	PRODUCT_ID,
	TANK_NAME,
	INS_USER
) VALUES (
	#{entity.productId},
	#{entity.tankName},
	#{entity.insUser}
)

mapper.update:
UPDATE tank


		PRODUCT_ID = #{entity.productId},


		TANK_NAME = #{entity.tankName},

	UPD_TIME = CURRENT_TIMESTAMP,
	UPD_USER = #{entity.updUser}


	ID = #{entity.id}

UPDATE tank

	DEL_FLAG = 1,
	DEL_TIME = CURRENT_TIMESTAMP,
	DEL_USER = #{entity.delUser}


	ID = #{entity.id}
